import {
  FETCH_BENEFITS_REQUEST,
  FETCH_BENEFITS_SUCCESS,
  FETCH_BENEFITS_FAILURE,
  APPEND_CATEGORY
} from "./benefitActionTypes";

const initialState = {
  loading: false,
  benefits: [],
  selectedBenefits: [],
  error: ""
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case FETCH_BENEFITS_REQUEST:
      return {
        ...state,
        loading: true
      };
    case FETCH_BENEFITS_SUCCESS:
      return {
        loading: false,
        benefits: action.payload,
        error: ""
      };
    case FETCH_BENEFITS_FAILURE:
      return {
        loading: false,
        benefits: [],
        error: action.payload
      };
    case APPEND_CATEGORY:
      console.log("Here >>> ", action);
      return {
        ...state,
        loading: false,
        selectedBenefits: action.payload,
        error: ""
      };
    default:
      return state;
  }
};

export default reducer;
